                b: "running" /* RUNNING */,
                c: "complete" /* COMPLETE */,
            });
            m
                .expect(o(taskInstance))
                .toBeObservable(m.cold('----(a|)', { a: 'somevalue' }));
        }));
        it('cancels on unsubscribe', marbles(m => {
            const taskInstance = new TaskInstance(of('somevalue').pipe(delay(m.time('----|'), m.scheduler)));
            expectState(m, taskInstance, '(ab)c---', {
                a: "pending" /* PENDING */,
                b: "running" /* RUNNING */,
                c: "cancelled" /* CANCELLED */,
            });
            m
                .expect(o(taskInstance), '----!')
                .toBeObservable(m.cold('-----', {}));
        }));
        it('switches to error state on error', marbles(m => {
            const taskInstance = new TaskInstance(throwError(new Error('Not good')));
            expectState(m, taskInstance, '(abc|)', {
                a: "pending" /* PENDING */,
                b: "running" /* RUNNING */,
                c: "error" /* ERROR */,
            });
            m
                .expect(o(taskInstance))
                .toBeObservable(m.cold('#', {}, new Error('Not good')));
        }));
    });
});

import { of } from 'rxjs/observable/of';
